package JavaPackage;

public class AssignmentOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int a = 10;
		
		System.out.println(a += 3);
		System.out.println(a -= 4);
		System.out.println(a *= 2);
		System.out.println(a /= 2);
	}

}
