package JavaPackage;

public class BreakStatement4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		
			int i = 1;
			do
			{
				if(i==5)
				{
					break;
					
				}
				System.out.println(i);
				i++;
							
				
			}
			while (i <= 10);
	}

}
